import React from 'react'
import Link from 'next/link'

export const Layout = ({ children, title }) => {
    return (
        <div className='container'>
            <nav>
                <Link href='/'>
                    <a><span className='main-title'>Hacker Next</span></a>
                </Link>
            </nav>
            {children}
            <style jsx>{`
                .container {
                    max-width: 800px;
                    margin: auto;
                    background: #f6f6ef;
                }
                nav {
                    background: #f60;
                    padding: 1em;
                }
                nav > * {
                    display: inline-block;
                    color: black;
                }
            `}</style>
        </div>
    )
}

export default Layout;
